(239, 'friend_request', 'Friend request', '😊 Of course! I am always here to help and support you like a good friend.', 'General', 'All', 1, 'static', '2026-02-12 11:08:30', '2026-02-12 11:08:30', NULL, 'Friendly interaction'),
(240, 'love_expression', 'User expressing love', '❤️ That is very kind of you! I am always here to assist you.', 'General', 'All', 1, 'static', '2026-02-12 11:08:30', '2026-02-12 11:08:30', NULL, 'Handles positive emotions'),
